##This function fetches the reserves reserve0 and reserve1
#of the address of the pair provided
def FetchReservesUniswap(pair):
    web3 = Web3(Web3.HTTPProvider(infura_url))
    contract = web3.eth.contract(address=Web3.toChecksumAddress(pair), abi=abi)
    reserves=contract.functions.getReserves().call()
    return reserves[0],reserves[1]

def FetchReservesQuickswap(pair):
    web3 = Web3(Web3.HTTPProvider(alchemy))
    contract = web3.eth.contract(address=Web3.toChecksumAddress(pair), abi=abi)
    reserves=contract.functions.getReserves().call()
    return reserves[0],reserves[1]

def FetchReservesApeswap(pair):
    web3 = Web3(Web3.HTTPProvider(alchemy))
    contract = web3.eth.contract(address=Web3.toChecksumAddress(pair), abi=abi)
    reserves=contract.functions.getReserves().call()
    return reserves[0],reserves[1]

def FetchReserves(selectedSwap,pair):

    if selectedSwap.Network=="Polygon":
        web3 = Web3(Web3.HTTPProvider(alchemy))
        contract = web3.eth.contract(address=Web3.toChecksumAddress(pair), abi=abi)
        for x in range(9999):
            try:
                reserves = contract.functions.getReserves().call()
                break
            except (requests.exceptions.HTTPError, ConnectionResetError) as e:
                if isinstance(e, requests.exceptions.HTTPError):
                    if e.response.status_code != 429:
                        # If the error is something other than Too Many Requests, re-raise it
                        raise
                # Wait a bit before retrying. The wait time increases with each retry
                time.sleep((2 ** x) + np.random.random())
        return reserves[0], reserves[1]

    elif selectedSwap.Network=="Ethereum":
        web3 = Web3(Web3.HTTPProvider(infura_url))
        contract = web3.eth.contract(address=Web3.toChecksumAddress(pair), abi=abi)
        reserves = contract.functions.getReserves().call()
        return reserves[0], reserves[1]
